import React, { useEffect, useState } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition/lib/SpeechRecognition';
import ScrollToBottom from "react-scroll-to-bottom"

function Dictaphone({ socket, username, room }) {

    const commands = [
        {
            command: 'reset',
            callback: () => resetTranscript()
        },

        {
            command: 'turn off',
            callback: () => SpeechRecognition.stopListening()
        },

    ]
    const {
        transcript,
        interimTranscript,
        finalTranscript,
        resetTranscript,
        listening,
    } = useSpeechRecognition({ commands });

    const [messageList, setMessageList] = useState([]);

    useEffect(() => {
        if (finalTranscript !== '') {
            console.log(finalTranscript);
        }
    }, [interimTranscript, finalTranscript]);

    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
        return <span>Your browser does not support speech recognition software!</span>
    }

    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
        console.log('Your browser does not support speech recognition software!');
    }
    const listenContinuously = () => {
        SpeechRecognition.startListening({
            continuous: true,
            language: 'en',
        });
        // const messageData = {
        //     room: room,
        //     author: username,
        //     message: finalTranscript
        // }
        // sendMessage()
        console.log(transcript);
        // await socket.emit("send_voice", messageData);
    };

    if (listening) {
        const messageData = {
            room: room,
            author: username,
            message: transcript
        }
        socket.emit("send_voice", messageData);
    }

    useEffect(() => {
        socket.on("receive_voice", (data) => {
            setMessageList((list) => [data]);
            console.log(data);
        });
    }, [socket]);

    return (
        <div className='chat-window'>
            <div>
                <span>
                    listening:
                    {' '}
                    {listening ? 'on' : 'off'}
                </span>
                <div>
                    <button type="button" onClick={listenContinuously}>Listen</button>
                    <button type="button" onClick={SpeechRecognition.stopListening}>Stop</button>
                    <button type="button" onClick={resetTranscript}>Reset</button>
                </div>
            </div>
            {/* <div>
                <span>{transcript}</span>
            </div> */}
            <div className='chat-body'>
                <ScrollToBottom className='message-container'>
                    <div className="message"
                        id={username === messageContent.author ? "you" : "other"}>
                        <div>
                            <div className='message-content'>
                                <p>{messageContent.message}</p>
                            </div>
                            <div className='message-meta'>
                                <p id="author">{messageContent.author}</p>
                            </div>
                        </div>
                    </div>
                </ScrollToBottom>
            </div>
        </div>
    );
};

export default Dictaphone;
